import{_ as t,c as o,o as i,aN as l}from"./chunks/framework.Cql-nzwB.js";const h=JSON.parse('{"title":"外汇交易基础（第二部分）","description":"","frontmatter":{},"headers":[],"relativePath":"price/03-forex-basics/03b.md","filePath":"price/03-forex-basics/03b.md","lastUpdated":null}'),n={name:"price/03-forex-basics/03b.md"};function s(a,r,e,g,u,d){return i(),o("div",null,[...r[0]||(r[0]=[l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /price\\03-forex-basics\\03b.md for this page in Markdown format</div><h1 id="外汇交易基础-第二部分" tabindex="-1">外汇交易基础（第二部分） <a class="header-anchor" href="#外汇交易基础-第二部分" aria-label="Permalink to &quot;外汇交易基础（第二部分）&quot;">​</a></h1><h2 id="交易成本与券商选择" tabindex="-1">交易成本与券商选择 <a class="header-anchor" href="#交易成本与券商选择" aria-label="Permalink to &quot;交易成本与券商选择&quot;">​</a></h2><ul><li><strong>盈利模式</strong>：券商主要通过两种方式盈利： <ul><li><strong>点差 (Spread)</strong>：即买入价与卖出价之间的差额。这是无佣金平台的主要收入来源。</li><li><strong>佣金 (Commission)</strong>：在点差之外收取的固定费用。</li></ul></li><li><strong>成本影响</strong>：过高的交易成本（如2-3个点的大点差）会使盈利变得非常困难。</li><li><strong>券商对比</strong>： <ul><li><strong>无佣金平台</strong>：通常点差较大，隐性成本高。</li><li><strong>佣金平台</strong>（如视频中提到的IB）：可能提供极小的点差（如0.5点），即使加上佣金，总交易成本反而可能更低。</li></ul></li><li><strong>核心建议</strong>：应选择信誉良好、规模大的知名券商，因为他们通常能提供更低的总交易成本，从而更容易实现盈利。</li></ul><h2 id="货币对与术语解读" tabindex="-1">货币对与术语解读 <a class="header-anchor" href="#货币对与术语解读" aria-label="Permalink to &quot;货币对与术语解读&quot;">​</a></h2><ul><li><strong>货币对构成</strong>： <ul><li><strong>基准货币 (Base Currency)</strong>：货币对中的第一个货币（如 <strong>EUR</strong>/USD 中的 EUR），是实际交易（买入或卖出）的对象。</li><li><strong>报价货币 (Quote Currency)</strong>：第二个货币（如 EUR/<strong>USD</strong> 中的 USD），用于为基准货币定价。</li></ul></li><li><strong>交易含义</strong>： <ul><li><strong>做多 EUR/USD</strong>：意味着买入欧元，同时卖出美元，预期欧元相对于美元升值。</li><li><strong>做空 EUR/JPY</strong>：意味着卖出欧元，同时买入日元，预期欧元相对于日元贬值。</li></ul></li><li><strong>专业术语</strong>：应使用标准货币代码，避免使用“电缆 (Cable)”或“囊鼠 (Gopher)”等行话，因为专业交易者不常用。</li></ul><h2 id="市场风险与诚信问题" tabindex="-1">市场风险与诚信问题 <a class="header-anchor" href="#市场风险与诚信问题" aria-label="Permalink to &quot;市场风险与诚信问题&quot;">​</a></h2><ul><li><strong>历史问题</strong>：过去，大型银行和公司存在利用“伦敦4点定盘价”窗口进行市场操纵的风险。他们通过合谋在短时间内设定人为高价或低价来牟利。</li><li><strong>现状</strong>：随着监管的加强和定盘窗口的延长（从1分钟延长至5分钟），此类操纵行为已大幅减少。目前的市场诚信度很高，普通交易者无需过度担忧此类系统性作弊风险。</li></ul><h2 id="价格报价-点-pip-与分数点" tabindex="-1">价格报价：点 (Pip) 与分数点 <a class="header-anchor" href="#价格报价-点-pip-与分数点" aria-label="Permalink to &quot;价格报价：点 (Pip) 与分数点&quot;">​</a></h2><ul><li><strong>定义</strong>：“点 (Pip)”是外汇市场价格最小的常规变动单位，类似于股票市场中的“跳动点 (Tick)”。</li><li><strong>标准</strong>： <ul><li><strong>多数货币对</strong> (如 EUR/USD)：点是小数点后<strong>第4位</strong>。</li><li><strong>日元相关货币对</strong> (如 USD/JPY)：点是小数点后<strong>第2位</strong>。</li></ul></li><li><strong>分数点 (Fractional Pips)</strong>：现代平台为了提供更精确的报价，在“点”后面增加了额外的小数位（如第5位或第3位）。但由于其变动过快，对交易决策意义不大，可以忽略。</li></ul><h2 id="交易表现的衡量标准" tabindex="-1">交易表现的衡量标准 <a class="header-anchor" href="#交易表现的衡量标准" aria-label="Permalink to &quot;交易表现的衡量标准&quot;">​</a></h2><ul><li><strong>通用语言</strong>：交易者之间习惯用“点数”而非具体金额来讨论盈亏（例如，“今天赚了25个点”）。</li><li><strong>主要目的</strong>： <ul><li><strong>保护隐私</strong>：避免透露个人账户规模和具体盈亏金额。</li><li><strong>标准化比较</strong>：提供一个不受仓位大小影响的、衡量交易技能的通用标准。</li></ul></li></ul>',12)])])}const p=t(n,[["render",s]]);export{h as __pageData,p as default};
