import{_ as a,c as i,o as r,aN as t}from"./chunks/framework.Cql-nzwB.js";const c=JSON.parse('{"title":"买压与卖压（第一部分）","description":"","frontmatter":{},"headers":[],"relativePath":"price/10-buying-selling-pressure/10a.md","filePath":"price/10-buying-selling-pressure/10a.md","lastUpdated":null}'),e={name:"price/10-buying-selling-pressure/10a.md"};function o(n,l,s,u,g,d){return r(),i("div",null,[...l[0]||(l[0]=[t('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /price\\10-buying-selling-pressure\\10a.md for this page in Markdown format</div><h1 id="买压与卖压-第一部分" tabindex="-1">买压与卖压（第一部分） <a class="header-anchor" href="#买压与卖压-第一部分" aria-label="Permalink to &quot;买压与卖压（第一部分）&quot;">​</a></h1><h2 id="买压与卖压基础" tabindex="-1">买压与卖压基础 <a class="header-anchor" href="#买压与卖压基础" aria-label="Permalink to &quot;买压与卖压基础&quot;">​</a></h2><h3 id="定义与识别" tabindex="-1">定义与识别 <a class="header-anchor" href="#定义与识别" aria-label="Permalink to &quot;定义与识别&quot;">​</a></h3><ul><li><strong>核心概念</strong>：通过观察K线形态和价格行为，判断当前是多头（买方）力量强，还是空头（卖方）力量强。</li><li><strong>买压 (Buying Pressure)</strong>：表明多头积极、强势买入，通常预示价格将上涨。 <ul><li><strong>迹象</strong>：更多、更大体积的阳线；连续阳线；阳线收盘在高位（接近最高点或至少中点以上）；K线底部有下影线；上涨窄通道；突破前期高点；制造缺口；阳线体积增大；连续阴线数量减少且体积变小。</li></ul></li><li><strong>卖压 (Selling Pressure)</strong>：表明空头积极、强势卖出，通常预示价格将下跌。 <ul><li><strong>迹象</strong>：更多、更大体积的阴线；连续阴线；阴线收盘在低位（接近最低点或至少中点以下）；K线顶部有上影线；下降窄通道；跌破前期低点；制造缺口；阴线体积增大；连续阳线数量减少且体积变小。</li></ul></li><li><strong>机构足迹</strong>：显著的买压或卖压通常反映了机构交易者的行为和共识，增加了趋势持续的可能性。</li></ul><h2 id="缺口-gaps" tabindex="-1">缺口 (Gaps) <a class="header-anchor" href="#缺口-gaps" aria-label="Permalink to &quot;缺口 (Gaps)&quot;">​</a></h2><h3 id="定义与类型" tabindex="-1">定义与类型 <a class="header-anchor" href="#定义与类型" aria-label="Permalink to &quot;定义与类型&quot;">​</a></h3><ul><li><strong>广义定义</strong>：支撑位和阻力位之间的任何&quot;空隙&quot;。</li><li><strong>常见类型</strong>： <ul><li><strong>跳空缺口 (Opening Gap)</strong>：开盘价与前一收盘价之间的大幅跳空。</li><li><strong>K线间缺口 (Bar Gaps)</strong>： <ul><li>一根K线的低点高于前期K线的高点（向上缺口）。</li><li>一根K线的高点低于前期K线的低点（向下缺口）。</li></ul></li><li><strong>收盘价缺口 (Closing Price Gaps)</strong>： <ul><li>K线收盘价高于前期K线的高点。</li><li>K线收盘价低于前期K线的低点。</li></ul></li><li><strong>负缺口 (Negative Gap / Body Gap)</strong>： <ul><li>突破后回调，影线短暂重叠突破点，但K线实体未重叠。</li><li>表示趋势仍然强劲，但不如真缺口可靠。</li></ul></li></ul></li><li><strong>Al Brooks 的特殊定义</strong>：有时会将某些高低点之间的区域也视为&quot;缺口&quot;（如下个单元详解）。</li></ul><h3 id="缺口的重要性" tabindex="-1">缺口的重要性 <a class="header-anchor" href="#缺口的重要性" aria-label="Permalink to &quot;缺口的重要性&quot;">​</a></h3><ul><li><strong>趋势强度信号</strong>：缺口（尤其是K线间缺口和收盘价缺口）是趋势强劲的重要标志。表明一方力量远超另一方，价格无需回撤即可继续。</li><li><strong>测量目标 (Measured Move - MM)</strong>：缺口（包括负缺口）经常作为测量目标位的起点。例如，突破颈线或失败形态（如失败双底）后，常以缺口或形态高度来预测目标位。</li><li><strong>支撑与阻力转换</strong>：突破形成的缺口区域，原阻力变为支撑，原支撑变为阻力。</li><li><strong>缺口回补</strong>：缺口有时会被回补。如果缺口很快被回补，可能意味着趋势强度减弱，市场可能进入震荡。</li></ul><h3 id="失败形态与缺口mm" tabindex="-1">失败形态与缺口MM <a class="header-anchor" href="#失败形态与缺口mm" aria-label="Permalink to &quot;失败形态与缺口MM&quot;">​</a></h3><ul><li><strong>失败双底/双顶</strong>：当市场尝试形成双底但失败并跌破低点（或尝试双顶失败并突破高点）时，可以将此形态的高度向下（或向上）翻倍，作为MM目标位。</li><li><strong>隐含失败形态</strong>：即使没有明显的反弹形成双底，只要价格在前期低点附近尝试支撑失败并跌破，也可视为&quot;失败的双底尝试&quot;，并应用MM原则。</li></ul><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h2><ul><li>持续观察买卖压力的变化，有助于判断趋势强度和可能的突破方向。</li><li>缺口是衡量趋势强度和预测价格目标的重要工具。</li></ul>',14)])])}const p=a(e,[["render",o]]);export{c as __pageData,p as default};
