import{_ as a,c as t,o as e,aN as i}from"./chunks/framework.Cql-nzwB.js";const c=JSON.parse('{"title":"突破导论","description":"","frontmatter":{},"headers":[],"relativePath":"price/15-breakouts/15a.md","filePath":"price/15-breakouts/15a.md","lastUpdated":null}'),o={name:"price/15-breakouts/15a.md"};function l(n,r,s,g,u,d){return e(),t("div",null,[...r[0]||(r[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /price\\15-breakouts\\15a.md for this page in Markdown format</div><h1 id="突破导论" tabindex="-1">突破导论 <a class="header-anchor" href="#突破导论" aria-label="Permalink to &quot;突破导论&quot;">​</a></h1><h2 id="突破的核心定义-core-definition-of-a-breakout" tabindex="-1">突破的核心定义 (Core Definition of a Breakout) <a class="header-anchor" href="#突破的核心定义-core-definition-of-a-breakout" aria-label="Permalink to &quot;突破的核心定义 (Core Definition of a Breakout)&quot;">​</a></h2><ul><li><strong>概念</strong>：突破是由一根或一系列趋势K线（即大阳线/大阴线）组成，其价格强势超越了某个支撑或阻力位。</li><li><strong>突破对象</strong>： <ul><li>前一根或多根K线的最高点/最低点。</li><li>趋势线或通道线。</li><li>移动平均线 (Moving Average, EMA20)。</li><li>前期的重要高点或低点（Swing High/Low）。</li></ul></li><li><strong>普遍性</strong>：图表上每一根大的趋势K线都是一次突破。突破的支撑/阻力位越多，信号越强。</li><li><strong>K线影线</strong>：每根K线的上、下影线都代表一次在更小时间周期上失败的突破尝试。</li></ul><h2 id="反转即突破-reversals-are-breakouts" tabindex="-1">反转即突破 (Reversals are Breakouts) <a class="header-anchor" href="#反转即突破-reversals-are-breakouts" aria-label="Permalink to &quot;反转即突破 (Reversals are Breakouts)&quot;">​</a></h2><ul><li><strong>内在联系</strong>：每一次反转（Reversal）的尝试，本质上都是一次反方向的突破尝试。</li><li><strong>示例</strong>：一轮上涨后的强力大阴线，既是多头突破失败的信号，也是一次空头突破（反转）的开始。</li><li><strong>分析视角</strong>：应将反转K线视为突破K线进行分析，关注其强度、收盘位置和后续K线的确认。</li></ul><h2 id="市场惯性-80-法则-market-inertia-the-80-rule" tabindex="-1">市场惯性：80% 法则 (Market Inertia: The 80% Rule) <a class="header-anchor" href="#市场惯性-80-法则-market-inertia-the-80-rule" aria-label="Permalink to &quot;市场惯性：80% 法则 (Market Inertia: The 80% Rule)&quot;">​</a></h2><ul><li><strong>核心思想</strong>：市场具有强大的惯性，倾向于维持当前状态，约80%的改变尝试会失败。</li><li><strong>具体应用</strong>： <ul><li><strong>震荡区间 (Trading Range)</strong>：约80%向上或向下的突破尝试会失败，价格会重新回到区间内。</li><li><strong>趋势 (Trend)</strong>：约80%的反转尝试会失败，原趋势会继续。</li></ul></li><li><strong>交易启示</strong>：这是价格行为分析的基石。在交易前，首先要判断市场处于趋势还是震荡，并假设当前状态会持续。</li></ul><h2 id="震荡区间中的突破交易策略-trading-strategy-for-breakouts-in-a-range" tabindex="-1">震荡区间中的突破交易策略 (Trading Strategy for Breakouts in a Range) <a class="header-anchor" href="#震荡区间中的突破交易策略-trading-strategy-for-breakouts-in-a-range" aria-label="Permalink to &quot;震荡区间中的突破交易策略 (Trading Strategy for Breakouts in a Range)&quot;">​</a></h2><ul><li><strong>核心策略</strong>：基于“80%的突破会失败”原则，在震荡区间的边界进行逆势交易（Fading）。</li><li><strong>具体操作</strong>： <ul><li><strong>高抛 (Sell High)</strong>：当价格以强劲阳线（抢购高潮, Buying Climax）冲向区间顶部时，将其视为陷阱，寻找做空机会。</li><li><strong>低吸 (Buy Low)</strong>：当价格以强劲阴线（抛售高潮, Selling Climax）砸向区间底部时，将其视为陷阱，寻找做多机会。</li></ul></li><li><strong>入场方式</strong>： <ul><li><strong>激进</strong>：直接在强突破K线的收盘价反向入场。</li><li><strong>稳健</strong>：等待反转信号K线出现后，再顺着反转方向入场。</li></ul></li><li><strong>交易目标</strong>：由于预计市场将继续震荡，主要目标是剥头皮（Scalp）获取短线利润。</li></ul><h2 id="总结原则" tabindex="-1">总结原则 <a class="header-anchor" href="#总结原则" aria-label="Permalink to &quot;总结原则&quot;">​</a></h2><ul><li><strong>突破无处不在</strong>：任何强趋势K线都是一次突破，反转也是突破的一种形式。</li><li><strong>惯性是关键</strong>：牢记80%法则——震荡区间的突破大多会失败，趋势中的反转大多会失败。</li><li><strong>情景决定策略</strong>：在震荡区间中，应逆向思考，利用失败的突破进行高抛低吸的剥头皮交易，而不是追涨杀跌。</li><li><strong>确认至上</strong>：突破的强度和其后的跟随K线（Follow-through）至关重要。糟糕的跟随是突破失败的强烈信号。如果不确定，最好的选择是等待。</li></ul>',12)])])}const k=a(o,[["render",l]]);export{c as __pageData,k as default};
