import{_ as a,c as e,o as t,aN as l}from"./chunks/framework.Cql-nzwB.js";const c=JSON.parse('{"title":"大反转（第四部分）：筑底、头肩形与失败反转","description":"","frontmatter":{},"headers":[],"relativePath":"price/22-major-trend-reversals/22d.md","filePath":"price/22-major-trend-reversals/22d.md","lastUpdated":null}'),o={name:"price/22-major-trend-reversals/22d.md"};function s(i,r,n,d,h,g){return t(),e("div",null,[...r[0]||(r[0]=[l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /price\\22-major-trend-reversals\\22d.md for this page in Markdown format</div><h1 id="大反转-第四部分-筑底、头肩形与失败反转" tabindex="-1">大反转（第四部分）：筑底、头肩形与失败反转 <a class="header-anchor" href="#大反转-第四部分-筑底、头肩形与失败反转" aria-label="Permalink to &quot;大反转（第四部分）：筑底、头肩形与失败反转&quot;">​</a></h1><h2 id="大反转底部-major-reversal-bottoms" tabindex="-1">大反转底部 (Major Reversal Bottoms) <a class="header-anchor" href="#大反转底部-major-reversal-bottoms" aria-label="Permalink to &quot;大反转底部 (Major Reversal Bottoms)&quot;">​</a></h2><h3 id="关键信号" tabindex="-1">关键信号 <a class="header-anchor" href="#关键信号" aria-label="Permalink to &quot;关键信号&quot;">​</a></h3><ul><li><strong>趋势线突破</strong>：首先需要看到下降趋势线被向上强势突破。</li><li><strong>强劲反弹</strong>： <ul><li><strong>特征</strong>：反弹最好能持续5-10根K线以上，包含强劲的阳线，并触及移动均线。</li><li><strong>均线缺口K线 (MA Gap Bar)</strong>：如果出现一根K线完全在移动均线上方，这是非常强的多头信号，表明买方力量足以控制市场。</li></ul></li><li><strong>最后一段下跌</strong>：出现上述强劲反弹后，接下来的下跌通常是熊市的最后一段。</li></ul><h3 id="交易逻辑" tabindex="-1">交易逻辑 <a class="header-anchor" href="#交易逻辑" aria-label="Permalink to &quot;交易逻辑&quot;">​</a></h3><ul><li><strong>寻找入场点</strong>：在市场测试前期低点时，寻找反转上涨的信号。 <ul><li><strong>低点抬高的大反转 (Higher Low Major Reversal)</strong></li><li><strong>双底大反转 (Double Bottom Major Reversal)</strong></li><li><strong>低点降低的大反转 (Lower Low Major Reversal)</strong></li></ul></li><li><strong>注意</strong>：如果此前的下跌是一个非常窄的通道（几乎没有买压），那么第一次反弹很可能只是小反弹，市场大概率进入震荡区间，而不是直接反转为上涨趋势。</li></ul><h2 id="头肩形态-head-and-shoulders-patterns" tabindex="-1">头肩形态 (Head and Shoulders Patterns) <a class="header-anchor" href="#头肩形态-head-and-shoulders-patterns" aria-label="Permalink to &quot;头肩形态 (Head and Shoulders Patterns)&quot;">​</a></h2><h3 id="结构分解" tabindex="-1">结构分解 <a class="header-anchor" href="#结构分解" aria-label="Permalink to &quot;结构分解&quot;">​</a></h3><ul><li><strong>头肩顶 (Head &amp; Shoulders Top)</strong>：本质是两个大反转形态的组合。 <ul><li><strong>左肩到头</strong>：通常是一个“高点抬高”的大反转。</li><li><strong>头到右肩</strong>：是一个“高点降低”的大反转。右肩无法超越头部，是多头力量衰竭的关键信号。</li></ul></li><li><strong>头肩底 (Head &amp; Shoulders Bottom)</strong>：与头肩顶逻辑相反。 <ul><li><strong>左肩到头</strong>：通常是一个“低点降低”的大反转。</li><li><strong>头到右肩</strong>：是一个“低点抬高”的大反转。右肩高于头部，是空头力量衰竭的关键信号。</li></ul></li><li><strong>嵌套结构</strong>：在大的头肩形态中，其组成部分（左肩、头、右肩）常常会包含更小级别的双顶/双底或迷你头肩形态。</li></ul><h2 id="失败的反转-failed-reversals" tabindex="-1">失败的反转 (Failed Reversals) <a class="header-anchor" href="#失败的反转-failed-reversals" aria-label="Permalink to &quot;失败的反转 (Failed Reversals)&quot;">​</a></h2><h3 id="识别与概率" tabindex="-1">识别与概率 <a class="header-anchor" href="#识别与概率" aria-label="Permalink to &quot;识别与概率&quot;">​</a></h3><ul><li><strong>高失败率</strong>：大反转最终能形成新趋势的概率只有约40%，意味着约60%的尝试会失败。</li><li><strong>失败信号</strong>：当一个反转形态（如头肩顶）形成后，价格没有朝预期的方向突破，反而向原趋势方向突破（例如，向上突破头肩顶的右肩）。</li></ul><h3 id="交易含义" tabindex="-1">交易含义 <a class="header-anchor" href="#交易含义" aria-label="Permalink to &quot;交易含义&quot;">​</a></h3><ul><li><strong>高概率顺势信号</strong>：失败的反转是一个非常可靠的<strong>顺势交易</strong>信号。</li><li><strong>背后的双重力量</strong>： <ul><li><strong>逆势交易者止损</strong>：尝试做反转的交易者（如在头肩顶做空）的止损被触发（空头回补买入），为突破火上浇油。</li><li><strong>顺势交易者入场</strong>：他们看到反转失败，确认原趋势将延续，于是积极入场。</li></ul></li><li><strong>交易目标</strong>： <ul><li>当反转失败时，预期价格将沿着原趋势方向走出至少<strong>两段式运动 (Two-Legged Move)</strong>。</li><li>可以使用**衡量运动 (Measured Move, MM)**来预估价格目标，通常基于失败形态的高度。</li></ul></li></ul><h2 id="总结原则" tabindex="-1">总结原则 <a class="header-anchor" href="#总结原则" aria-label="Permalink to &quot;总结原则&quot;">​</a></h2><ul><li><strong>筑底需要买压</strong>：有效的大反转底部需要有信服力的买方力量证明，如强劲反弹和均线缺口K线。</li><li><strong>头肩形是力量的转换</strong>：头肩形态的本质是趋势力量的逐步转换，右肩的相对高低点是关键确认信号。</li><li><strong>失败是更强的信号</strong>：一个形态的失败，通常会因原有参与者的止损而演变成一个更强劲、更可靠的顺势交易机会。</li></ul>',17)])])}const m=a(o,[["render",s]]);export{c as __pageData,m as default};
