import{bV as r,aL as l,u as t,G as o}from"./chunks/framework.SimCp1Tr.js";const c=JSON.parse('{"title":"楔形（第四部分）","description":"","frontmatter":{},"headers":[],"relativePath":"price/24-wedges/24d.md","filePath":"price/24-wedges/24d.md","lastUpdated":null}'),e={name:"price/24-wedges/24d.md"};function i(n,a,s,d,g,h){return l(),t("div",null,[...a[0]||(a[0]=[o('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /price/24-wedges/24d.md for this page in Markdown format</div><h1 id="楔形-第四部分" tabindex="-1">楔形（第四部分） <a class="header-anchor" href="#楔形-第四部分" aria-label="Permalink to &quot;楔形（第四部分）&quot;">​</a></h1><h2 id="抛物线楔形-parabolic-wedges" tabindex="-1">抛物线楔形 (Parabolic Wedges) <a class="header-anchor" href="#抛物线楔形-parabolic-wedges" aria-label="Permalink to &quot;抛物线楔形 (Parabolic Wedges)&quot;">​</a></h2><h3 id="定义与特征" tabindex="-1">定义与特征 <a class="header-anchor" href="#定义与特征" aria-label="Permalink to &quot;定义与特征&quot;">​</a></h3><ul><li><strong>概念</strong>：一种斜率不断增加（越来越陡峭）的趋势，通常以三推（3-push）的形式出现，本质上是一系列连续的抢购或抛售高潮。</li><li><strong>形态</strong>： <ul><li><strong>加速运动</strong>：趋势线越来越陡，是动能即将耗尽的信号。</li><li><strong>窄通道</strong>：尽管是楔形，但通道通常非常狭窄，有时看起来更像微通道 (microchannel)。</li><li><strong>波段缺口</strong>：强势的抛物线楔形中，回调通常不会触及前一波段的高/低点，形成缺口。</li><li><strong>最终突破</strong>：第三推（或最后一推）常常会加速突破通道线，然后反转。</li></ul></li></ul><h3 id="交易含义" tabindex="-1">交易含义 <a class="header-anchor" href="#交易含义" aria-label="Permalink to &quot;交易含义&quot;">​</a></h3><ul><li><strong>市场过热信号</strong>：表示市场“走得太远、太快”，是不可持续的。</li><li><strong>逆势交易机会</strong>：激进交易者会押注其反转，但风险高。 <ul><li><strong>早期入场</strong>：在第一个反转信号 K 线下做空（或上方做多），风险/回报比较好，但胜率低。</li><li><strong>确认后入场</strong>：等待强力反转K线（如连续阴/阳线）出现后再入场，胜率高，但止损远，风险/回报比较差。</li></ul></li><li><strong>心理博弈</strong>：抛物线顶部的反转，通常是套住了追高的多头，他们被迫止损离场，从而加剧了下跌。</li></ul><h2 id="强趋势中的楔形陷阱" tabindex="-1">强趋势中的楔形陷阱 <a class="header-anchor" href="#强趋势中的楔形陷阱" aria-label="Permalink to &quot;强趋势中的楔形陷阱&quot;">​</a></h2><h3 id="现象与误区" tabindex="-1">现象与误区 <a class="header-anchor" href="#现象与误区" aria-label="Permalink to &quot;现象与误区&quot;">​</a></h3><ul><li><strong>现象</strong>：在非常强劲、通道极窄的趋势中（即“微通道”），会不断出现看似“楔形”的三推结构。</li><li><strong>误区</strong>：新手交易者会把这些小结构当成反转信号，不断尝试逆势交易（如在强劲的下降微通道中不断抄底）。</li></ul><h3 id="核心原则-80-法则" tabindex="-1">核心原则 (80% 法则) <a class="header-anchor" href="#核心原则-80-法则" aria-label="Permalink to &quot;核心原则 (80% 法则)&quot;">​</a></h3><ul><li><strong>法则</strong>：在强趋势中，80% 的反转尝试都会失败。</li><li><strong>本质</strong>：这些所谓的“楔形”并不是真正的反转形态，而只是趋势延续过程中的旗形 (Flag)。</li><li><strong>判断依据</strong>： <ul><li><strong>通道宽度</strong>：通道过于狭窄，说明趋势力量极强，缺乏有意义的逆势压力。</li><li><strong>更高时间周期</strong>：在更高时间周期图表上，这种窄通道通常只表现为一两根强劲的突破 K 线。</li></ul></li></ul><h3 id="正确策略" tabindex="-1">正确策略 <a class="header-anchor" href="#正确策略" aria-label="Permalink to &quot;正确策略&quot;">​</a></h3><ul><li><strong>放弃逆势</strong>：在窄通道中，绝对不要考虑逆势交易。</li><li><strong>顺势而为</strong>：将每一次回调都视为旗形，等待回调结束、出现顺势信号时入场（例如，在下降趋势的反弹结束后，在阴线下方做空）。</li></ul><h2 id="从高潮到反转的演变" tabindex="-1">从高潮到反转的演变 <a class="header-anchor" href="#从高潮到反转的演变" aria-label="Permalink to &quot;从高潮到反转的演变&quot;">​</a></h2><h3 id="无尽的回调-endless-pullback" tabindex="-1">无尽的回调 (Endless Pullback) <a class="header-anchor" href="#无尽的回调-endless-pullback" aria-label="Permalink to &quot;无尽的回调 (Endless Pullback)&quot;">​</a></h3><ul><li><strong>概念</strong>：在一次强劲的趋势（如抛物线高潮）后，出现一个持续时间过长（如超过20根 K 线）的窄通道回调。</li><li><strong>含义</strong>：原先的牛旗/熊旗逐渐失去动力，市场进入多空平衡状态 (50/50)，原趋势反转的概率大增。</li><li><strong>交易策略</strong>：耐心等待这个“无尽的回调”通道被明确突破。一旦出现强力突破和良好跟随，即可确认原趋势的终结和新趋势的开始。</li></ul><h3 id="识别有效反转" tabindex="-1">识别有效反转 <a class="header-anchor" href="#识别有效反转" aria-label="Permalink to &quot;识别有效反转&quot;">​</a></h3><ul><li><strong>前提</strong>：并非所有楔形都能成功反转。趋势的最终反转通常需要更强的逆势信号。</li><li><strong>关键信号</strong>： <ul><li><strong>买卖压力</strong>：出现有分量的反转 K 线（如实体饱满的阳线/阴线），显示出真正的逆势力度。</li><li><strong>结构确认</strong>：等待一个更可靠的反转结构，如双重顶/底，或在第一波强力反转后的更高低点/更低高点（即 H2/L2 信号）。</li></ul></li><li><strong>谨慎抄底/摸顶</strong>：在窄通道趋势中，仅凭一个小的三推楔形就逆势交易，是低胜率行为。必须等待趋势强度明显减弱的证据。</li></ul><h2 id="楔形与通道概率" tabindex="-1">楔形与通道概率 <a class="header-anchor" href="#楔形与通道概率" aria-label="Permalink to &quot;楔形与通道概率&quot;">​</a></h2><h3 id="基本概率" tabindex="-1">基本概率 <a class="header-anchor" href="#基本概率" aria-label="Permalink to &quot;基本概率&quot;">​</a></h3><ul><li><strong>楔形顶 (上涨通道/熊旗)</strong>：约 <strong>75%</strong> 的概率会向下跌破（至少演变为震荡区间），仅 <strong>25%</strong> 的概率会加速向上突破。</li><li><strong>楔形底 (下降通道/牛旗)</strong>：约 <strong>75%</strong> 的概率会向上突破，仅 <strong>25%</strong> 的概率会加速向下跌破。</li></ul><h3 id="交易应用" tabindex="-1">交易应用 <a class="header-anchor" href="#交易应用" aria-label="Permalink to &quot;交易应用&quot;">​</a></h3><ul><li><strong>突破不等于新趋势</strong>：通道的突破（75%概率事件）仅表示原通道结束，市场可能进入震荡区间，而不一定立即开启反向趋势。</li><li><strong>关注支撑与阻力</strong>：反转的目标位通常是前期的关键高/低点或测算目标位 (Measured Move)。在这些位置，趋势交易者会止盈，可能导致回调或盘整。</li><li><strong>警惕小概率事件</strong>：虽然概率小，但向上/下加速突破（25%概率事件）一旦发生，通常会引发非常强劲的趋势行情。</li></ul>',24)])])}const b=r(e,[["render",i]]);export{c as __pageData,b as default};
